import React, { PureComponent } from 'react'
import PropTypes from 'prop-types'
import Box from 'ui-box'
import { Button } from '../../buttons'
import { TextInput } from '../../text-input'
import safeInvoke from '../../lib/safe-invoke'

export const CLASS_PREFIX = 'evergreen-file-picker'

export default class FilePicker extends PureComponent {
  static propTypes = {
    /**
     * Name attribute of the input.
     */
    name: PropTypes.string,

    /**
     * The accept attribute of the input.
     */
    accept: PropTypes.oneOfType([
      PropTypes.string,
      PropTypes.arrayOf(PropTypes.string)
    ]),

    /**
     * When true, the file picker is required.
     */
    required: PropTypes.bool,

    /**
     * When true, accept multiple files.
     */
    multiple: PropTypes.bool,

    /**
     * When true, the filepicker is disabled.
     */
    disabled: PropTypes.bool,

    /**
     * The capture attribute of the input.
     */
    capture: PropTypes.bool,

    /**
     * The height of the file picker.
     */
    height: PropTypes.number,

    /**
     * Function called when onChange is fired
     */
    onChange: PropTypes.func,

    /**
     * Function called when onBlur is fired
     */
    onBlur: PropTypes.func,

    /**
     * Placeholder of the text input
     */
    placeholder: PropTypes.string
  }

  static defaultProps = {
    placeholder: 'Select a file to upload…'
  }

  constructor() {
    super()

    this.state = {
      files: []
    }
  }

  render() {
    const {
      name,
      accept,
      required,
      multiple,
      disabled,
      capture,
      height,
      onChange, // Remove onChange from props
      placeholder,
      ...props
    } = this.props
    const { files } = this.state

    let inputValue
    if (files.length === 0) {
      inputValue = ''
    } else if (files.length === 1) {
      inputValue = files[0].name
    } else {
      inputValue = `${files.length} files`
    }

    let buttonText
    if (files.length === 0) {
      buttonText = 'Select file'
    } else if (files.length === 1) {
      buttonText = 'Replace file'
    } else {
      buttonText = 'Replace files'
    }

    return (
      <Box display="flex" className={`${CLASS_PREFIX}-root`} {...props}>
        <Box
          innerRef={this.fileInputRef}
          className={`${CLASS_PREFIX}-file-input`}
          is="input"
          type="file"
          name={name}
          accept={accept}
          required={required}
          multiple={multiple}
          disabled={disabled}
          capture={capture}
          onChange={this.handleFileChange}
          display="none"
        />

        <TextInput
          className={`${CLASS_PREFIX}-text-input`}
          readOnly
          value={inputValue}
          placeholder={placeholder}
          // There's a weird specifity issue when there's two differently sized inputs on the page
          borderTopRightRadius="0 !important"
          borderBottomRightRadius="0 !important"
          height={height}
          flex={1}
          textOverflow="ellipsis"
          onBlur={this.handleBlur}
        />

        <Button
          className={`${CLASS_PREFIX}-button`}
          onClick={this.handleButtonClick}
          disabled={disabled}
          borderTopLeftRadius={0}
          borderBottomLeftRadius={0}
          height={height}
          flexShrink={0}
          type="button"
          onBlur={this.handleBlur}
        >
          {buttonText}
        </Button>
      </Box>
    )
  }

  fileInputRef = node => {
    this.fileInput = node
  }

  handleFileChange = e => {
    // Firefox returns the same array instance each time for some reason
    const files = [...e.target.files]

    this.setState({ files })

    safeInvoke(this.props.onChange, files)
  }

  handleButtonClick = () => {
    this.fileInput.click()
  }

  handleBlur = e => {
    // Setting e.target.files to an array fails. It must be a FileList
    if (e && e.target) e.target.files = this.fileInput && this.fileInput.files

    safeInvoke(this.props.onBlur, e)
  }
}
